import { createContext, useContext, useEffect, useState } from "react";
import axios from "../services/api"; // Make sure this has baseURL + withCredentials: true

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // To show spinner if needed

  // Check if user is logged in on first load
  useEffect(() => {
    axios.get("/api/auth/me", { withCredentials: true })
      .then(res => {
        setUser(res.data);
      })
      .catch(() => {
        setUser(null);
      })
      .finally(() => setLoading(false));
  }, []);

  // Login (save user state after success)
  const login = async (credentials) => {
    const res = await axios.post("/api/auth/login", credentials, {
      withCredentials: true
    });
    setUser(res.data.user);
    return res;
  };

  // Logout (clear session)
  const logout = async () => {
    await axios.post("/api/auth/logout", {}, { withCredentials: true });
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{
      user,
      login,
      logout,
      isAuthenticated: !!user,
      loading
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
