// src/components/RosterPreview.js
import React from 'react';
import './rosterPreview.css';

const RosterPreview = ({ rosterData }) => {
  if (!rosterData || rosterData.length === 0) return null;

  return (
    <div className="roster-preview-container">
      <h3>ðŸ“‹ Roster Preview</h3>
      <div className="roster-scroll">
        <table className="roster-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Day</th>
              <th>OOD/Battery</th>
              <th>Phone</th>
              <th>Comments</th>
              <th>A Duty/Battery</th>
              <th>Phone</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody>
            {rosterData.map((row, idx) => {
              const isWeekend = ['Friday', 'Saturday', 'Sunday'].includes(row.day);
              return (
                <tr key={idx} className={isWeekend ? 'weekend-row' : ''}>
                  <td>{row.date}</td>
                  <td>{row.day}</td>
                  <td>{row.ood}</td>
                  <td>{row.oodPhone || '-'}</td>
                  <td>{row.oodComment}</td>
                  <td>{row.aDuty}</td>
                  <td>{row.aDutyPhone || '-'}</td>
                  <td>{row.aDutyComment}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default RosterPreview;
