import React, {useState} from 'react';
import './employeeForm.css';
const ranks = ['LCpl', 'Cpl', 'Sgt', 'SSgt', 'GySgt'];
const batteries = ['A', 'B', 'C', 'H&S'];

const EmployeeForm = ({ employeeInput, setEmployeeInput, handleAddEmployee }) => {
    const [leaveDate, setLeaveDate] = useState('');

  const addLeaveDate = () => {
    if (leaveDate && !employeeInput.leaves.includes(leaveDate)) {
      setEmployeeInput({
        ...employeeInput,
        leaves: [...employeeInput.leaves, leaveDate],
      });
      setLeaveDate('');
    }
  };

  const removeLeaveDate = (date) => {
    setEmployeeInput({
      ...employeeInput,
      leaves: employeeInput.leaves.filter(d => d !== date),
    });
  };
  return (
    <><div className="form-grid">
          <div>
              <label>Name</label>
              <input
                  type="text"
                  placeholder="Name"
                  value={employeeInput.name}
                  onChange={(e) => setEmployeeInput({ ...employeeInput, name: e.target.value })} />
          </div>
          <div>
              <label>Rank</label>
              <select
                  value={employeeInput.rank}
                  onChange={(e) => setEmployeeInput({ ...employeeInput, rank: e.target.value })}
              >
                  <option value="">Select Rank</option>
                  {ranks.map(rank => <option key={rank} value={rank}>{rank}</option>)}
              </select>
          </div>
          <div>
              <label>Battery</label>
              <select
                  value={employeeInput.battery}
                  onChange={(e) => setEmployeeInput({ ...employeeInput, battery: e.target.value })}
              >
                  <option value="">Select Battery</option>
                  {batteries.map(bat => <option key={bat} value={bat}>{bat}</option>)}
              </select>
          </div>
          <div className="leave-picker">
        <input
          type="date"
          value={leaveDate}
          onChange={(e) => setLeaveDate(e.target.value)}
        />
        <button type="button" onClick={addLeaveDate}>➕ Add Leave</button>
      </div>

      {employeeInput.leaves.length > 0 && (
        <ul className="leave-list">
          {employeeInput.leaves.map((date, idx) => (
            <li key={idx}>
              {date} <button onClick={() => removeLeaveDate(date)}>❌</button>
            </li>
          ))}
        </ul>
      )}
      </div><button onClick={handleAddEmployee}>➕ Add Employee</button></>
  );
};

export default EmployeeForm;
