import React from 'react';
import './PreviewRoster.css';

const PreviewRoster = ({ rosterData }) => {
  if (!rosterData || rosterData.length === 0) return null;

  const isWeekend = (day) => ['Friday', 'Saturday', 'Sunday'].includes(day);

  return (
    <div className="preview-container">
      <h2>ðŸ“‹ Roster Preview</h2>
      <div className="roster-table-wrapper">
        <table className="roster-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Day</th>
              <th>OOD/Battery</th>
              <th>Phone</th>
              <th>Comments</th>
              <th>A Duty/Battery</th>
              <th>Phone</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody>
            {rosterData.map((row, idx) => (
              <tr
                key={idx}
                className={isWeekend(row.day) ? 'weekend-row' : ''}
              >
                <td>{row.date}</td>
                <td>{row.day}</td>
                <td>{row.ood || '-'}</td>
                <td>{row.oodPhone || '-'}</td>
                <td>{row.oodComment || '-'}</td>
                <td>{row.aduty || '-'}</td>
                <td>{row.adutyPhone || '-'}</td>
                <td>{row.adutyComment || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PreviewRoster;
