import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import EmployeeForm from '../components/employeeForm';
import EmployeeList from '../components/employeeList';
import '../App.css';
import './home.css'

const Home = () => {
  const [userId, setUserId] = useState('');
  const [employees, setEmployees] = useState([]);
  const [employeeInput, setEmployeeInput] = useState({ name: '', rank: '', battery: '', leaves: [] });
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const [darkMode, setDarkMode] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    document.body.className = darkMode ? 'dark-mode' : '';
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);
  useEffect(() => {
    const storedId = localStorage.getItem('userId');
    if (storedId) {
      setUserId(storedId);
    } else {
      const newId = uuidv4();
      localStorage.setItem('userId', newId);
      setUserId(newId);
    }
  }, []);

  const handleAddEmployee = () => {
    if (!employeeInput.name || !employeeInput.rank || !employeeInput.battery) return;
    setEmployees([...employees, { ...employeeInput, leaves: employeeInput.leaves || [] }]);
    setEmployeeInput({ name: '', rank: '', battery: '', leaves: [] });
  };

  const handleSaveDraft = async () => {
    try {
      await axios.post('http://localhost:3001/api/roster/save-draft', {
        userId,
        employees,
        startDate,
        endDate,
      });
      alert('Draft saved successfully!');
    } catch (err) {
      console.error(err);
      alert('Error saving draft.');
    }
  };

  const handleGenerateRoster = async () => {
    try {
      const res = await axios.post('http://localhost:3001/api/roster/generate', {
        employees,
        startDate,
        endDate,
      }, { responseType: 'blob' });

      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'Shift_Roster.xlsx');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      console.error(err);
      alert('Error generating roster.');
    }
  };

  return (
    <div className="container">
      <h1 className='heading'>Firewatch Roster Builder</h1>

      <button onClick={() => setDarkMode(!darkMode)} className="mr-2">
  {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
</button>
      <EmployeeForm
        employeeInput={employeeInput}
        setEmployeeInput={setEmployeeInput}
        handleAddEmployee={handleAddEmployee}
      />

      <div className="form-group">
        <label>Start Date: </label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        <label>End Date: </label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
      </div>

      <div className="form-group">
        <button onClick={handleSaveDraft}>💾 Save Draft</button>
        <button onClick={handleGenerateRoster}>📥 Generate Roster</button>
      </div>

      <EmployeeList employees={employees} />
    </div>
  );
};

export default Home;
